import React, { Component } from "react";
import Navigation from "./Navigation";
import { Typography, Select, MenuItem } from "@material-ui/core";

class Contact extends Component {
  state = { projectRequest: false, requestedProjects: [] };
  render() {
    let spacer = {
      height: "0",
      width: "100%",
    };

    const projects = [
      "Euchre",
      "Fakebook",
      "CPU Sim",
      "Wheel of Fortune",
      "Budget Tracker",
      "Insta485 (Server side dynamic)",
      "Insta485 (Client side dynamic)",
      "MapReduce Server",
      "Search Engine",
      "Crypto Project",
      "Web Security (SQL injections, CSRF, XSS)",
      "MITM Attacks",
      "Appsec and Buffer Overflows",
    ];

    handleChange = (event) => {
      this.setState({ requestedProjects: event.target.value });
    };

    return (
      <div>
        <Navigation />
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            height: "100vh",
            flexWrap: "wrap",
          }}
        >
          <div>
            <Typography variant="h6" align="center">
              Want to get ahold of me?
            </Typography>
            <div className={spacer}></div>
            <Typography variant="body2" color="textSecondary">
              This form will automatically send an email to an address you can
              reach me, and if you need a response I'll get back to you.
            </Typography>
            <div className={spacer} style={{ height: "10%" }}></div>
            <Select
              multiple
              value={this.state.requestedProjects}
              onChange={handleChange}
            >
              {projects.map((name) => (
                <MenuItem key={name} value={name}>
                  {name}
                </MenuItem>
              ))}
            </Select>
          </div>
        </div>
      </div>
    );
  }
}

export default Contact;
