import React, { Component } from "react";
import Navigation from "./Navigation";
import {
  Typography,
  Select,
  MenuItem,
  Input,
  InputLabel,
} from "@material-ui/core";

class Contact extends Component {
  state = { projectRequest: false, requestedProjects: [] };
  render() {
    let spacer = {
      height: "0",
      width: "100%",
    };

    const ITEM_HEIGHT = 48;
    const ITEM_PADDING_TOP = 8;
    const MenuProps = {
      PaperProps: {
        style: {
          maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
          width: 250,
        },
      },
    };

    const projects = [
      "Euchre",
      "Fakebook",
      "CPU Sim",
      "Wheel of Fortune",
      "Budget Tracker",
      "Insta485 (Server side dynamic)",
      "Insta485 (Client side dynamic)",
      "MapReduce Server",
      "Search Engine",
      "Crypto Project",
      "Web Security",
      "MITM Attacks",
      "Appsec and Buffer Overflows",
    ];

    const handleChange = (event) => {
      this.setState({
        requestedProjects: event.target.value,
      });
    };

    return (
      <div>
        <Navigation />
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            height: "100vh",
            flexWrap: "wrap",
          }}
        >
          <div>
            <Typography variant="h6" align="center">
              Want to get ahold of me?
            </Typography>
            <div className={spacer}></div>
            <Typography variant="body2" color="textSecondary">
              This form will walk you through requesting a project, giving my
              website feedback, just sending a message, or any combination of
              the above.
            </Typography>
            <div className={spacer} style={{ height: "10%" }}></div>
            <div
              style={{
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
              }}
            >
              <Select
                style={{ width: "15%", alignSelf: "center" }}
                multiple
                value={this.state.requestedProjects}
                onChange={handleChange}
                MenuProps={MenuProps}
                input={<Input />}
              >
                {projects.map((name) => (
                  <MenuItem key={name} value={name}>
                    {name}
                  </MenuItem>
                ))}
              </Select>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Contact;
