import React, { Component } from "react";
import Navigation from "./Navigation";
import {
  Typography,
  Select,
  MenuItem,
  Grid,
  TextField,
  Button,
  Backdrop,
  CircularProgress,
} from "@material-ui/core";

import Rating from "@material-ui/lab/Rating";
import $ from "jquery";

class Contact extends Component {
  state = {
    requestedProjects: [],
    rating: "0",
    name: "",
    email: "",
    message: "",
    buttonDisabled: true,
    emailDisabled: true,
    backDrop: false,
  };
  render() {
    let spacer = {
      height: "0",
      width: "100%",
    };

    const ITEM_HEIGHT = 48;
    const ITEM_PADDING_TOP = 8;
    const MenuProps = {
      PaperProps: {
        style: {
          maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
          width: 250,
        },
      },
    };

    const projects = [
      "Euchre",
      "Fakebook",
      "CPU Sim",
      "Wheel of Fortune",
      "Budget Tracker",
      "Insta485 (Server side dynamic)",
      "Insta485 (Client side dynamic)",
      "MapReduce Server",
      "Search Engine",
      "Crypto Project",
      "Web Security",
      "MITM Attacks",
      "Appsec and Buffer Overflows",
    ];

    const handleChange = (event) => {
      this.setState({
        requestedProjects: event.target.value,
      });
    };

    const handleRate = (event) => {
      this.setState({
        rating: event.target.value,
      });
    };

    const handleName = (event) => {
      let disable = event.target.value.replace(/\s+/g, "") === "";
      this.setState({
        name: event.target.value,
        emailDisabled: disable,
      });
    };

    const handleEmail = (event) => {
      this.setState({
        email: event.target.value,
      });
    };

    const handleMessage = (event) => {
      this.setState({
        message: event.target.value,
        buttonDisabled: false,
      });
    };

    const openBackDrop = () => {
      this.setState({
        backDrop: true,
      });
    };

    const sendEmail = () => {
      var data = {
        service_id: "service_l8vuzla",
        template_id: "template_7j0vt7g",
        user_id: "user_PRcS8QDBhOBb6tsZjUiR5",
        template_params: {
          from_name: this.state.name,
          message: this.state.message,
          rating: this.state.rating,
          requested: this.state.requestedProjects.toString(),
        },
      };
      $.ajax("https://api.emailjs.com/api/v1.0/email/send", {
        type: "POST",
        data: JSON.stringify(data),
        contentType: "application/json",
      })
        .done(function () {
          alert("Your mail is sent!");
          this.setState({
            requestedProjects: [],
            rating: "0",
            name: "",
            email: "",
            message: "",
            buttonDisabled: true,
            emailDisabled: true,
            backDrop: false,
          });
        })
        .fail(function (error) {
          alert("Oops... " + JSON.stringify(error));
        });
    };

    if (window.screen.availWidth < 828) {
      return (
        <div>
          <Navigation />
          <Typography variant="h4" align="center" style={{ marginTop: "3vh" }}>
            Want to get in touch?
          </Typography>
          <div className={spacer}></div>
          <div
            style={{
              display: "flex",
              justifyContent: "center",
              marginRight: "3%",
              marginLeft: "3%",
              height: "80vh",
              flexWrap: "wrap",
            }}
          >
            <div>
              <div style={{ height: "5vh" }}></div>
              <Typography variant="h6" align="center">
                Select projects to request
              </Typography>
