import React, { Component } from "react";
import Navigation from "./Navigation";
import {
  Typography,
  Select,
  MenuItem,
  Input,
  Grid,
  TextField,
  Button,
} from "@material-ui/core";
import Rating from "@material-ui/lab/Rating";

class Contact extends Component {
  state = {
    projectRequest: false,
    requestedProjects: [],
    rating: 0,
    name: "",
    email: "",
    message: "",
  };
  render() {
    let spacer = {
      height: "0",
      width: "100%",
    };

    const ITEM_HEIGHT = 48;
    const ITEM_PADDING_TOP = 8;
    const MenuProps = {
      PaperProps: {
        style: {
          maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
          width: 250,
        },
      },
    };

    const projects = [
      "Euchre",
      "Fakebook",
      "CPU Sim",
      "Wheel of Fortune",
      "Budget Tracker",
      "Insta485 (Server side dynamic)",
      "Insta485 (Client side dynamic)",
      "MapReduce Server",
      "Search Engine",
      "Crypto Project",
      "Web Security",
      "MITM Attacks",
      "Appsec and Buffer Overflows",
    ];

    const handleChange = (event) => {
      this.setState({
        requestedProjects: event.target.value,
      });
    };

    const handleRate = (event) => {
      this.setState({
        rating: event.target.value,
      });
    };

    const handleName = (event) => {
      this.setState({
        name: event.target.value,
      });
    };

    const handleEmail = (event) => {
      this.setState({
        email: event.target.value,
      });
    };

    const handleMessage = (event) => {
      this.setState({
        message: event.target.value,
      });
    };

    return (
      <div>
        <Navigation />
        <Typography variant="h4" align="center" style={{ marginTop: "3vh" }}>
          Want to get ahold of me?
        </Typography>
        <div className={spacer}></div>
        <div
          style={{
            display: "flex",
            justifyContent: "center",

            height: "100vh",
            flexWrap: "wrap",
          }}
        >
          <div>
            <div style={{ height: "5vh", width: "100%" }}></div>
            <Typography variant="h6" align="center">
              Select projects to request
            </Typography>
            <div className={spacer}></div>
            <Typography variant="body2" color="textSecondary" align="center">
              Optional - I Will only comply if I can verify you are not, or
              never will be, a college student who could use this to cheat.
            </Typography>
            <div
              style={{
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                marginTop: "2vh",
              }}
            >
              <Select
                style={{ width: "20%", alignSelf: "center" }}
                multiple
                value={this.state.requestedProjects}
                onChange={handleChange}
                MenuProps={MenuProps}
                input={<Input />}
              >
                {projects.map((name) => (
                  <MenuItem key={name} value={name}>
                    {name}
                  </MenuItem>
                ))}
              </Select>
            </div>
            <Typography
              variant="h6"
              align="center"
              style={{ marginTop: "2vh" }}
            >
              Rate my Website!
            </Typography>
            <div className={spacer}></div>
            <Typography variant="body2" color="textSecondary" align="center">
              Again, optional, but I really appreciate feedback and tips/ideas
              to try!
            </Typography>
            <div
              style={{
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
              }}
            >
              <Rating
                name="simple-controlled"
                value={this.state.rating}
                onChange={handleRate}
              />
            </div>
            <Typography
              variant="h6"
              align="center"
              style={{ marginTop: "2vh" }}
            >
              Leave your name and a return address
            </Typography>
            <div className={spacer}></div>
            <Grid container spacing={4}>
              <Grid
                item
                xs={6}
                style={{ display: "flex", justifyContent: "center" }}
              >
                <TextField
                  id="outlined-basic"
                  label="Name"
                  variant="outlined"
                  onChange={handleName}
                />
              </Grid>
              <Grid
                item
                xs={6}
                style={{ display: "flex", justifyContent: "center" }}
              >
                <TextField
                  id="outlined-basic"
                  label="Email"
                  variant="outlined"
                  onChange={handleEmail}
                />
              </Grid>
            </Grid>
            <div
              style={{
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                flexWrap: "wrap",
              }}
            >
              <TextField
                id="outlined-basic"
                label="And finally, a message"
                variant="outlined"
                multiline
                style={{ marginTop: "3vh", width: "100%" }}
              />
              <div className={spacer}></div>
              <Button>Send</Button>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Contact;
